class HomeFrag : Fragment(){


    lateinit var sharedPref : SharedPreferences
    lateinit var sharedPrefEdit : SharedPreferences.Editor

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        var abc : View = inflater.inflate(R.layout.db_home_frag, container, false)
        if(abc==null){
            Log.e("Error", "View Null")
            return inflater.inflate(R.layout.db_home_frag, container, false)
        }
        val sliderView: SliderView = abc.findViewById(R.id.imageSlider) as SliderView

        if(activity == null){
            Log.e("Error", "Activity Null")
            return inflater.inflate(R.layout.db_home_frag, container, false)
        }
        val adapter = SliderAdapterDb(activity?.applicationContext as Context)

        sliderView.setSliderAdapter(adapter)

        sliderView.setIndicatorAnimation(IndicatorAnimationType.WORM) //set indicator animation by using SliderLayout.IndicatorAnimations. :WORM or THIN_WORM or COLOR or DROP or FILL or NONE or SCALE or SCALE_DOWN or SLIDE and SWAP!!

        sliderView.setSliderTransformAnimation(SliderAnimations.SIMPLETRANSFORMATION)
        sliderView.setAutoCycleDirection(SliderView.AUTO_CYCLE_DIRECTION_BACK_AND_FORTH)
        sliderView.setIndicatorSelectedColor(Color.WHITE)
        sliderView.setIndicatorUnselectedColor(Color.GRAY)
        sliderView.setScrollTimeInSec(4) //set scroll delay in seconds :

        sliderView.startAutoCycle()
        var si = SliderItem()
        si.description = "Khakhra"
        si.imageUrl = "data:image/jpeg;base64,"
        adapter.addItem(si)

        var si2 = SliderItem()
        si2.description = "Pickle"
        si2.imageUrl = "data:image/jpeg;base64,"
        adapter.addItem(si2)

        var si3 = SliderItem()
        si3.description = "Gathiya"
        si3.imageUrl = "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSztDERfZFJYiOoC66ReChZoRCh-mMWt-54IA&usqp=CAU"
        adapter.addItem(si3)


//        searchBtnDb.setOnClickListener{
//            startActivity(Intent(this@Dashboard, SearchActivity::class.java))
//        }
//        micBtnDb.setOnClickListener{
//            startActivity(Intent(this@Dashboard, SearchActivity::class.java))
//        }
//        editSearchDb.setOnClickListener{
//            startActivity(Intent(this@Dashboard, SearchActivity::class.java))
//        }

        sharedPref = activity?.getSharedPreferences("com.example.gruhudhyog", Context.MODE_PRIVATE) as SharedPreferences
        sharedPrefEdit = sharedPref.edit()

        Toast.makeText(activity?.applicationContext as Context, sharedPref.getString("loginNum", null).toString(), Toast.LENGTH_SHORT).show()



//        drawer.openDrawer(GravityCompat.START)

        //TEMP




        return abc
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)







    }
